import React, { useState, useEffect } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

const UserStoreList = () => {
  const [stores, setStores] = useState([]);
  const [rating, setRating] = useState('');
  const [review, setReview] = useState('');
  const [selectedStoreId, setSelectedStoreId] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchStores = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get(`${process.env.REACT_APP_API_URL}/stores`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setStores(response.data);
      } catch (err) {
        setError('Failed to load stores');
      }
    };
    fetchStores();
  }, []);

  const handleRateStore = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      await axios.post(
        `${process.env.REACT_APP_API_URL}/ratings`,
        { storeId: selectedStoreId, rating: Number(rating), review },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setRating('');
      setReview('');
      setSelectedStoreId(null);
      alert('Rating submitted!');
    } catch (err) {
      setError('Failed to submit rating');
    }
  };

  return (
    <div className="card p-4 shadow-sm">
      <h2 className="card-title">Stores</h2>
      {error && <div className="alert alert-danger">{error}</div>}
      <ul className="list-group mb-4">
        {stores.map((store) => (
          <li key={store.id} className="list-group-item d-flex justify-content-between align-items-center">
            {store.name} - {store.address}
            <button
              className="btn btn-primary btn-sm"
              onClick={() => setSelectedStoreId(store.id)}
            >
              Rate
            </button>
          </li>
        ))}
      </ul>
      {selectedStoreId && (
        <form onSubmit={handleRateStore}>
          <h3>Rate Store</h3>
          <div className="mb-3">
            <label htmlFor="rating" className="form-label">Rating (1-5)</label>
            <input
              type="number"
              className="form-control"
              id="rating"
              min="1"
              max="5"
              value={rating}
              onChange={(e) => setRating(e.target.value)}
              required
            />
          </div>
          <div className="mb-3">
            <label htmlFor="review" className="form-label">Review</label>
            <textarea
              className="form-control"
              id="review"
              value={review}
              onChange={(e) => setReview(e.target.value)}
              required
            ></textarea>
          </div>
          <button type="submit" className="btn btn-primary">Submit Rating</button>
          <button
            type="button"
            className="btn btn-secondary ms-2"
            onClick={() => setSelectedStoreId(null)}
          >
            Cancel
          </button>
        </form>
      )}
    </div>
  );
};

export default UserStoreList;
