import React, { useState, useEffect } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

const OwnerDashboard = () => {
  const [stores, setStores] = useState([]);
  const [name, setName] = useState('');
  const [address, setAddress] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchStores = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get(`${process.env.REACT_APP_API_URL}/stores`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setStores(response.data);
      } catch (err) {
        setError('Failed to load stores');
      }
    };
    fetchStores();
  }, []);

  const handleCreateStore = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(
        `${process.env.REACT_APP_API_URL}/stores`,
        { name, address },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setStores([...stores, response.data]);
      setName('');
      setAddress('');
    } catch (err) {
      setError('Failed to create store');
    }
  };

  const handleDeleteStore = async (id) => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`${process.env.REACT_APP_API_URL}/stores/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setStores(stores.filter((store) => store.id !== id));
    } catch (err) {
      setError('Failed to delete store');
    }
  };

  return (
    <div className="card p-4 shadow-sm">
      <h2 className="card-title">Store Owner Dashboard</h2>
      {error && <div className="alert alert-danger">{error}</div>}
      <h3>Create Store</h3>
      <form onSubmit={handleCreateStore} className="mb-4">
        <div className="mb-3">
          <label htmlFor="name" className="form-label">Store Name</label>
          <input
            type="text"
            className="form-control"
            id="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>
        <div className="mb-3">
          <label htmlFor="address" className="form-label">Address</label>
          <input
            type="text"
            className="form-control"
            id="address"
            value={address}
            onChange={(e) => setAddress(e.target.value)}
            required
          />
        </div>
        <button type="submit" className="btn btn-primary">Create Store</button>
      </form>
      <h3>Your Stores</h3>
      <ul className="list-group">
        {stores.map((store) => (
          <li key={store.id} className="list-group-item d-flex justify-content-between align-items-center">
            {store.name} - {store.address}
            <button className="btn btn-danger btn-sm" onClick={() => handleDeleteStore(store.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default OwnerDashboard;
