import React, { useState, useEffect } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

const AdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [stores, setStores] = useState([]);
  const [ratings, setRatings] = useState([]);
  const [error, setError] = useState('');
  const [newUser, setNewUser] = useState({ name: '', email: '', address: '', password: '', role: 'USER' });
  const [filterName, setFilterName] = useState('');
  const [filterEmail, setFilterEmail] = useState('');
  const [filterAddress, setFilterAddress] = useState('');
  const [filterRole, setFilterRole] = useState('');
  const [sortField, setSortField] = useState('name');
  const [sortOrder, setSortOrder] = useState('asc');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          setError('No authentication token found. Please log in again.');
          return;
        }

        // Fetch users
        const userResponse = await axios.get(`${process.env.REACT_APP_API_URL}/users`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        let fetchedUsers = Array.isArray(userResponse.data) ? userResponse.data : [];

        // Fetch stores
        const storeResponse = await axios.get(`${process.env.REACT_APP_API_URL}/stores`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        let fetchedStores = Array.isArray(storeResponse.data) ? storeResponse.data : [];

        // Fetch ratings
        const ratingResponse = await axios.get(`${process.env.REACT_APP_API_URL}/ratings`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        const fetchedRatings = Array.isArray(ratingResponse.data) ? ratingResponse.data : [];
        setRatings(fetchedRatings);

        // Calculate overall rating for each store
        fetchedStores = fetchedStores.map(store => {
          const storeRatings = fetchedRatings.filter(r => r.storeId === store.id);
          const overallRating = storeRatings.length
            ? (storeRatings.reduce((sum, r) => sum + r.rating, 0) / storeRatings.length).toFixed(1)
            : 'N/A';
          return { ...store, overallRating };
        });

        // Apply filters to users
        if (filterName) {
          fetchedUsers = fetchedUsers.filter(user =>
            user.name.toLowerCase().includes(filterName.toLowerCase())
          );
        }
        if (filterEmail) {
          fetchedUsers = fetchedUsers.filter(user =>
            user.email.toLowerCase().includes(filterEmail.toLowerCase())
          );
        }
        if (filterAddress) {
          fetchedUsers = fetchedUsers.filter(user =>
            user.address.toLowerCase().includes(filterAddress.toLowerCase())
          );
        }
        if (filterRole) {
          fetchedUsers = fetchedUsers.filter(user => user.role === filterRole);
        }

        // Apply sorting to users
        fetchedUsers.sort((a, b) => {
          const fieldA = a[sortField]?.toString().toLowerCase() || '';
          const fieldB = b[sortField]?.toString().toLowerCase() || '';
          if (sortOrder === 'asc') {
            return fieldA > fieldB ? 1 : -1;
          }
          return fieldA < fieldB ? 1 : -1;
        });

        setUsers(fetchedUsers);
        setStores(fetchedStores);
      } catch (err) {
        const status = err.response?.status;
        if (status === 401) {
          setError('Authentication failed. Please log in again.');
          localStorage.removeItem('token');
        } else {
          setError(err.response?.data?.message || 'Failed to load data');
        }
      }
    };
    fetchData();
  }, [filterName, filterEmail, filterAddress, filterRole, sortField, sortOrder]);

  const handleCreateUser = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      await axios.post(
        `${process.env.REACT_APP_API_URL}/auth/register`,
        newUser,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      const userResponse = await axios.get(`${process.env.REACT_APP_API_URL}/users`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers(Array.isArray(userResponse.data) ? userResponse.data : []);
      setNewUser({ name: '', email: '', address: '', password: '', role: 'USER' });
      alert('User created!');
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to create user');
    }
  };

  const handleDeleteUser = async (id) => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`${process.env.REACT_APP_API_URL}/users/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers(users.filter(user => user.id !== id));
      alert('User deleted!');
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to delete user');
    }
  };

  const handleDeleteStore = async (id) => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`${process.env.REACT_APP_API_URL}/stores/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setStores(stores.filter(store => store.id !== id));
      setRatings(ratings.filter(rating => rating.storeId !== id));
      alert('Store deleted!');
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to delete store');
    }
  };

  return (
    <div className="card p-4 shadow-sm">
      <h2 className="card-title">Admin Dashboard</h2>
      {error && <div className="alert alert-danger">{error}</div>}
      <h3>Statistics</h3>
      <p>Total Users: {users.length}</p>
      <p>Total Stores: {stores.length}</p>
      <p>Total Ratings: {ratings.length}</p>
      <h3>Create User</h3>
      <form onSubmit={handleCreateUser}>
        <div className="mb-3">
          <label htmlFor="userName" className="form-label">Name</label>
          <input
            type="text"
            className="form-control"
            id="userName"
            value={newUser.name}
            onChange={(e) => setNewUser({ ...newUser, name: e.target.value })}
            required
          />
        </div>
        <div className="mb-3">
          <label htmlFor="userEmail" className="form-label">Email</label>
          <input
            type="email"
            className="form-control"
            id="userEmail"
            value={newUser.email}
            onChange={(e) => setNewUser({ ...newUser, email: e.target.value })}
            required
          />
        </div>
        <div className="mb-3">
          <label htmlFor="userAddress" className="form-label">Address</label>
          <input
            type="text"
            className="form-control"
            id="userAddress"
            value={newUser.address}
            onChange={(e) => setNewUser({ ...newUser, address: e.target.value })}
            required
          />
        </div>
        <div className="mb-3">
          <label htmlFor="userPassword" className="form-label">Password</label>
          <input
            type="password"
            className="form-control"
            id="userPassword"
            value={newUser.password}
            onChange={(e) => setNewUser({ ...newUser, password: e.target.value })}
            required
          />
        </div>
        <div className="mb-3">
          <label htmlFor="userRole" className="form-label">Role</label>
          <select
            className="form-select"
            id="userRole"
            value={newUser.role}
            onChange={(e) => setNewUser({ ...newUser, role: e.target.value })}
          >
            <option value="USER">Normal User</option>
            <option value="STORE_OWNER">Store Owner</option>
            <option value="ADMIN">Admin</option>
          </select>
        </div>
        <button type="submit" className="btn btn-primary">Create User</button>
      </form>
      <h3 className="mt-4">Users</h3>
      <div className="mb-3">
        <input
          type="text"
          className="form-control mb-2"
          placeholder="Filter by Name"
          value={filterName}
          onChange={(e) => setFilterName(e.target.value)}
        />
        <input
          type="text"
          className="form-control mb-2"
          placeholder="Filter by Email"
          value={filterEmail}
          onChange={(e) => setFilterEmail(e.target.value)}
        />
        <input
          type="text"
          className="form-control mb-2"
          placeholder="Filter by Address"
          value={filterAddress}
          onChange={(e) => setFilterAddress(e.target.value)}
        />
        <select
          className="form-select mb-2"
          value={filterRole}
          onChange={(e) => setFilterRole(e.target.value)}
        >
          <option value="">Filter by Role</option>
          <option value="USER">Normal User</option>
          <option value="STORE_OWNER">Store Owner</option>
          <option value="ADMIN">Admin</option>
        </select>
        <select
          className="form-select mb-2"
          value={sortField}
          onChange={(e) => setSortField(e.target.value)}
        >
          <option value="name">Sort by Name</option>
          <option value="email">Sort by Email</option>
          <option value="address">Sort by Address</option>
        </select>
        <select
          className="form-select mb-2"
          value={sortOrder}
          onChange={(e) => setSortOrder(e.target.value)}
        >
          <option value="asc">Ascending</option>
          <option value="desc">Descending</option>
        </select>
      </div>
      {users.length === 0 ? (
        <p>No users found.</p>
      ) : (
        <ul className="list-group mb-4">
          {users.map(user => (
            <li key={user.id} className="list-group-item">
              <div className="d-flex justify-content-between align-items-center">
                <div>
                  <strong>{user.name}</strong> - {user.email}<br />
                  Address: {user.address}<br />
                  Role: {user.role}
                  {user.role === 'STORE_OWNER' && (
                    <>
                      <br />
                      Average Rating: {stores.filter(s => s.ownerId === user.id).map(s => s.overallRating).join(', ') || 'N/A'}
                    </>
                  )}
                </div>
                <button
                  className="btn btn-danger btn-sm"
                  onClick={() => handleDeleteUser(user.id)}
                >
                  Delete
                </button>
              </div>
            </li>
          ))}
        </ul>
      )}
      <h3>Stores</h3>
      {stores.length === 0 ? (
        <p>No stores found.</p>
      ) : (
        <ul className="list-group">
          {stores.map(store => (
            <li key={store.id} className="list-group-item">
              <div className="d-flex justify-content-between align-items-center">
                <div>
                  <strong>{store.name}</strong> - {store.address}<br />
                  Overall Rating: {store.overallRating}<br />
                  Owner Email: {users.find(u => u.id === store.ownerId)?.email || 'N/A'}
                </div>
                <button
                  className="btn btn-danger btn-sm"
                  onClick={() => handleDeleteStore(store.id)}
                >
                  Delete
                </button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default AdminDashboard;
