import React, { useState, useEffect } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

const AdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [stores, setStores] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('token');
        const config = { headers: { Authorization: `Bearer ${token}` } };
        const [usersRes, storesRes] = await Promise.all([
          axios.get(`${process.env.REACT_APP_API_URL}/users`, config),
          axios.get(`${process.env.REACT_APP_API_URL}/stores`, config),
        ]);
        setUsers(usersRes.data || []);
        setStores(storesRes.data || []);
      } catch (err) {
        setError(err.response?.data?.message || 'Failed to load data');
      }
    };
    fetchData();
  }, []);

  const handleDeleteUser = async (id) => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`${process.env.REACT_APP_API_URL}/users/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers(users.filter((user) => user.id !== id));
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to delete user');
    }
  };

  const handleDeleteStore = async (id) => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`${process.env.REACT_APP_API_URL}/stores/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setStores(stores.filter((store) => store.id !== id));
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to delete store');
    }
  };

  return (
    <div className="card p-4 shadow-sm">
      <h2 className="card-title">Admin Dashboard</h2>
      {error && <div className="alert alert-danger">{error}</div>}
      <h3>Users</h3>
      {users.length === 0 ? (
        <p>No users found.</p>
      ) : (
        <ul className="list-group mb-4">
          {users.map((user) => (
            <li key={user.id} className="list-group-item d-flex justify-content-between align-items-center">
              {user.name} ({user.email}) - {user.role}
              <button className="btn btn-danger btn-sm" onClick={() => handleDeleteUser(user.id)}>Delete</button>
            </li>
          ))}
        </ul>
      )}
      <h3>Stores</h3>
      {stores.length === 0 ? (
        <p>No stores found.</p>
      ) : (
        <ul className="list-group">
          {stores.map((store) => (
            <li key={store.id} className="list-group-item d-flex justify-content-between align-items-center">
              {store.name} - {store.address}
              <button className="btn btn-danger btn-sm" onClick={() => handleDeleteStore(store.id)}>Delete</button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default AdminDashboard;
